window.addEventListener("DOMContentLoaded",()=>{let scrollY=window.pageYOffset;window.addEventListener("scroll",(function(){scrollY=window.pageYOffset}));let vh=window.innerHeight;window.onresize=()=>{vh=window.innerHeight};const anchorLinks=document.querySelectorAll('a[href^="#"]'),anchorLinksArr=Array.prototype.slice.call(anchorLinks);anchorLinksArr.forEach(link=>{link.addEventListener("click",e=>{e.preventDefault();const targetId=link.hash,targetElement=document.querySelector(targetId),targetOffsetTop=scrollY+targetElement.getBoundingClientRect().top;window.scrollTo({top:targetOffsetTop,behavior:"smooth"})})});const fadeElems=document.querySelectorAll(".fade"),fadeElemsArr=Array.prototype.slice.call(fadeElems);let fadePosFromBottom,fadePosFromTop=vh-vh/2;fadeElemsArr.forEach(fade=>{let fadeOffsetTop=fade.getBoundingClientRect().top-fadePosFromTop,hasActiveClass=!1;window.addEventListener("scroll",()=>{!hasActiveClass&&scrollY>=fadeOffsetTop&&(fade.className+=" isActive",hasActiveClass=!0)})})});